import "./App.scss"
import ColorBox from "./components/ColorBox"

function App() {
	const [todoList, setTodoList] = useState([
		{id: 1, title: 'I love Javascript'},
		{id: 2, title: 'We love ReactJS'},
		{id: 3, title: 'They love NodeJS'}
	]);
	return (
		<div className="app">
			<h1>React Hooks</h1>
			<ColorBox />
		</div>
	)
}

export default App
